export default dataHistory


export function getAllDataHistory() {
    return dataHistory
}

export function getDataAge() {
    for (let item of dataHistory) {
        return item.battles
    }
}


export function getAgebyId(idAge) {
    return dataHistory.find((item) => item.id == idAge)
}
export function getBattlebyId(idAge, idBattles) {
    const detail = getAgebyId(idAge)
    const battle = detail.battles.find(item => item.idBattles == idBattles)
    return battle
}

